import React from 'react';
import BookCard from './BookCard';
import styles from './BookList.module.css';

const BookList = ({ books = [] }) => {
  // Transform Open Library data to match our BookCard component expectations
  const transformedBooks = books.map(book => ({
    id: book.key,
    title: book.title,
    author: book.author_name,
    price: Math.floor(Math.random() * 20) + 10, // Random price for demo
    image: book.cover_i 
      ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
      : 'https://via.placeholder.com/150x200?text=No+Cover',
    description: `Published in ${book.first_publish_year || 'Unknown year'}`
  }));

  return (
    <div className={styles.bookListContainer}>
      <h2 className={styles.bookListTitle}>
        {books.length > 0 ? `Found ${books.length} Books` : 'Available Books'}
      </h2>
      {books.length === 0 && (
        <p className={styles.noBooksMessage}>
          Search for books to see results here.
        </p>
      )}
      <div className={styles.bookGrid}>
        {transformedBooks.map((book) => (
          <BookCard key={book.id} book={book} />
        ))}
      </div>
    </div>
  );
};

export default BookList;
