import React from 'react';
import BookCard from './BookCard';
import styles from './BookList.module.css';

const BookList = ({ books = [] }) => {
  // Mock data for demonstration - will be replaced with API data later
  const mockBooks = [
    {
      id: 1,
      title: 'The Great Gatsby',
      author: 'F. Scott Fitzgerald',
      price: 12.99,
      image: 'https://via.placeholder.com/150x200?text=Book+Cover',
      description: 'A classic novel about the American Dream.'
    },
    {
      id: 2,
      title: 'To Kill a Mockingbird',
      author: 'Harper Lee',
      price: 14.99,
      image: 'https://via.placeholder.com/150x200?text=Book+Cover',
      description: 'A powerful story about racial injustice.'
    },
    {
      id: 3,
      title: '1984',
      author: 'George Orwell',
      price: 11.99,
      image: 'https://via.placeholder.com/150x200?text=Book+Cover',
      description: 'A dystopian novel about totalitarianism.'
    }
  ];

  const displayBooks = books.length > 0 ? books : mockBooks;

  return (
    <div className={styles.bookListContainer}>
      <h2 className={styles.bookListTitle}>Available Books</h2>
      <div className={styles.bookGrid}>
        {displayBooks.map((book) => (
          <BookCard key={book.id} book={book} />
        ))}
      </div>
    </div>
  );
};

export default BookList;
