import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import BookList from './components/BookList';
import './App.css';

function App() {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (searchTerm) => {
    // TODO: Implement actual search functionality with API
    console.log('Searching for:', searchTerm);
    // For now, just log the search term
    // Later this will call an API and update searchResults
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="container">
          <h1>Online Kitap Magazasi</h1>
          <p>Your Online Bookstore</p>
        </div>
      </header>
      
      <main className="App-main">
        <div className="container">
          <SearchBar onSearch={handleSearch} />
          <BookList books={searchResults} />
        </div>
      </main>
      
      <footer className="App-footer">
        <div className="container">
          <p>&copy; 2024 Online Kitap Magazasi. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
